import React from 'react'; 
import axios from "axios"; 

let userSkills = [];  //storing the user's skills 
class Search extends React.Component {
    constructor(props) {
        super(props); 
        this.state = {
            data: [],
            matches: [],
            errorMessage: '',
        } 
    }
    // fetching data from backend
    async componentDidMount() {
        try {
            const res = await axios.get("/api/skills")
            let skillsArray = res.data.map(skill => skill.skill_name) 
            this.setState({
                data: skillsArray 
            })
        } catch(error){
            console.log(error)
        }
    }
    //function to match characters. 
    onChangedEvent = (e) => {
        let value = e.target.value; //capture the char as user types
        value = value.toLowerCase(); 
        let matches  = []; 
        //matching input value to stored data
        if (value.length > 0) {
            const regex = new RegExp(`^${value}`); 
            matches = this.state.data.sort().filter(val => regex.test(val))
            fetchData(matches)
        }
       this.setState(()=> ({matches})); 
    }

