import React from 'react'; 
import axios from "axios"; 
import {Link} from "react-router-dom"; 

let skills = []; 

class Search extends React.Component {
    constructor(props) {
        super(props); 
        this.state = {
            data: [], //holds skills data
            matches: [], 
            skills, //user skills
            result: [], //holds the jobs results
            errorMessage: '',
        } 
    }

    // fetching skills from database and updating it to data in state. 
    async componentDidMount() {
        try {
            const res = await axios.get("/api/skills")
            let skillsArray = res.data.map(skill => skill.skill_name) 
            this.setState({
                data: skillsArray,
            })
            console.log(res.data);
        } catch(error){
            console.log(error)
        }
    }

    //matching input skill to skills stored in data
    onChangedEvent = (e) => {
        let value = e.target.value; //capture the char as user types
        value = value.toLowerCase(); 
        let matches  = []; 
        //matching input value to stored data
        if (value.length > 0) {
            const regex = new RegExp(`^${value}`); 
            matches = this.state.data.sort().filter(val => regex.test(val))
        }
        //storing matched skills to matches and updating the state. 
       this.setState(()=> ({matches}));
    }

    //outputs results of matched skills to user. 
    outputResults () {
        const { matches } = this.state; 
        if (matches.length ===  0 ) {
            return null; 
        } 
        return (
            <ul> 
                {matches.map((item) => <li key={item}>{item}</li>)}
            </ul>
        )
    }

    //handling errors 
    handleEnterKey = (e) => {
        const { matches } = this.state;
        if(e.key === 'Enter' && matches.length > 0) {
            skills.push(e.target.value); 
        } else if(e.key === 'Enter' && matches.length === 0) {
            this.setState({
                errorMessage: "Skill does not exist in the database"
            })
         } else if(e.key === 'Backspace') {
            this.setState({
                errorMessage: ""
            })
        }
    }

    fetchResults = async skills => {
        let resultsArray = []; 
        skills.map(async function(skill) {
            try {
                const res = await axios.get(`/api/results/${skill}`); 
                var resultsArray = res.data.map(result => result.jobs_name) 
                console.log(resultsArray)
            } catch(error) {
                console.log(error);
        }   })
        this.setState({
            result: resultsArray
        })
    }
    render () {
        return (
            <div>  
                <input onChange={this.onChangedEvent} onKeyDown={(e) => this.handleEnterKey(e)} type="text"/> 
                {this.outputResults()}
                {this.state.errorMessage} 
                <button type="button" onClick={()=> this.fetchResults(this.state.skills)}> Search</button>
                {this.state.result}
            </div>
        )
    }
}
export default Search;
