import React from 'react'; 
import axios from "axios"; 

let userSkills = [];  //storing the user's skills 
class Search extends React.Component {
    constructor(props) {
        super(props); 
        this.state = {
            matched: '', 
            data: [],
            matches: [],
            errorMessage: '',
        } 
    }
    // fetching data from backend
    async componentDidMount() {
        try {
            const res = await axios.get("/api/skills")
            let skillsArray = res.data.map(skill => skill.skill_name) 
            this.setState({
                data: skillsArray 
            })
        } catch(error){
            console.log(error)
        }
    }

    fetchData = async arr => {
        try {
            await axios.get(`/api/skills/${arr}`); 
            console.log(`skillname : ${arr} was passed matched`); 
            const res = await axios.get('/api/skills') 
            this.setState({
                matched: arr[0]
            })
            console.log(this.state.matched, "matched") 
        } catch(error) {
            console.log(error)
        }
    }

    //function to match characters. 
    onChangedEvent = (e) => {
        let value = e.target.value; //capture the char as user types
        value = value.toLowerCase(); 
        let matches  = []; 
        //matching input value to stored data
        if (value.length > 0) {
            const regex = new RegExp(`^${value}`); 
            matches = this.state.data.sort().filter(val => regex.test(val))
            this.fetchData(matches)
        }
       this.setState(()=> ({matches})); 
    }

    outputResults () {
        const { matches } = this.state; 
        //if nothing is matched then output null. 
        if (matches.length ===  0 ) {
            return null; 
        } 
        //return the matched array. 
        return (
            <ul> 
                {matches.map((item) => <li key={item}>{item}</li>)}
            </ul>
        )
    }

    handleEnterKey = (e) => {
        const { matches } = this.state;
        if(e.key === 'Enter' && matches.length > 0) {
            userSkills.push(e.target.value); 
        } else if(e.key === 'Enter' && matches.length === 0) {
            this.setState({
                errorMessage: "error"
            })
         } else if(e.key === 'Backspace') {
            this.setState({
                errorMessage: ""
            })
        }
        console.log(userSkills)
    }

    render () {
        return (
            <div>  
                <input onChange={this.onChangedEvent} onKeyDown={(e) => this.handleEnterKey(e)} type="text"/> 
                {this.outputResults()}
                {this.state.errorMessage} 
            </div>
        )
    }
}

export default Search;
